<?php

namespace AppBundle\Repository;

/**
 * DiaSemanaRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class DiaSemanaRepository extends \Doctrine\ORM\EntityRepository
{
    public function findByDayNumber($number){
        $em=$this->getEntityManager();
        $dia="";
        switch($number){
            case 1:
                $dia="lunes";
                break;
            case 2:
                $dia="martes";
                break;
            case 3:
                $dia="miercoles";
                break;
            case 4:
                $dia="jueves";
                break;
            case 5:
                $dia="viernes";
                break;
            case 6:
                $dia="sabado";
                break;
            case 7:
                $dia="domingo";
                break;
        }
        return $em->createQuery("select dia from AppBundle:DiaSemana dia where dia.slug=:dia")->setParameter('dia',$dia)->getOneOrNullResult();
    }
}
